CREATE DATABASE IF NOT EXISTS `chatgpt_clone` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

USE `chatgpt_clone`;

-- 用户表
CREATE TABLE IF NOT EXISTS `m_user` (
    `id` INTEGER NOT NULL AUTO_INCREMENT,
    `username` VARCHAR(64) NOT NULL,
    `password` VARCHAR(64) NOT NULL,
    `nickname` VARCHAR(64) DEFAULT NULL,
    `email` VARCHAR(255) DEFAULT NULL,
    `phone` VARCHAR(64) DEFAULT NULL,
    `gender` TINYINT DEFAULT 0,
    `role` VARCHAR(100) DEFAULT 'admin',
    `is_deleted` TINYINT DEFAULT 0,
    `created_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    `updated_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (`id`),
    UNIQUE KEY `username` (`username`)
) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_unicode_ci;

-- 对话表
CREATE TABLE IF NOT EXISTS `m_dialog` (
    `id` INTEGER NOT NULL AUTO_INCREMENT,
    `user_id` INTEGER NOT NULL,
    `name` VARCHAR(64) DEFAULT NULL,
    `is_deleted` TINYINT DEFAULT 0,
    `created_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    `updated_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (`id`),
    KEY `user_id` (`user_id`)
) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_unicode_ci;

-- 消息表
CREATE TABLE IF NOT EXISTS `m_message` (
    `id` INTEGER NOT NULL AUTO_INCREMENT,
    `dialog_id` INTEGER NOT NULL,
    `user_id` INTEGER NOT NULL,
    `answer_id` INTEGER DEFAULT NULL,
    `question` VARCHAR(2046) DEFAULT NULL,
    `answer` VARCHAR(2046) DEFAULT NULL,
    `is_deleted` TINYINT DEFAULT 0,
    `created_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    `updated_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (`id`),
    KEY `dialog_id` (`dialog_id`),
    KEY `user_id` (`user_id`),
    KEY `answer_id` (`answer_id`)
) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_unicode_ci;

-- 答案表
CREATE TABLE IF NOT EXISTS `m_answer` (
    `id` INTEGER NOT NULL AUTO_INCREMENT,
    `field` VARCHAR(64) DEFAULT NULL,
    `keyword` VARCHAR(255) NOT NULL,
    `answer` VARCHAR(2046) NOT NULL,
    `is_deleted` TINYINT DEFAULT 0,
    `created_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    `updated_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
    PRIMARY KEY (`id`)
) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COLLATE = utf8mb4_unicode_ci;



